function Example() {
  const [label, setLabel] = React.useState(undefined);

  React.useEffect(() => {
    setTimeout(() => {
      setLabel(label === '99+' ? undefined : '99+');
    }, 2500);
  }, [label]);

  return (
    <BadgeAnchor>
      <Button raised label="Button" />
      <Badge label={label} />
    </BadgeAnchor>
  );
}
