function Example() {
  const [selected, setSelected] = React.useState(false);
  return (
    <ChipSet>
      <Chip
        key="my-chip"
        label="Click Me"
        checkmark
        selected={selected}
        onRemove={evt => console.log('onRemove', evt.detail)}
        onInteraction={evt => {
          console.log('onInteraction', evt.detail);
          setSelected(!selected);
        }}
        onTrailingIconInteraction={evt =>
          console.log('onTrailingIconIteraction', evt.detail)
        }
        trailingIcon="close"
      />
    </ChipSet>
  );
}

function Example() {
  const [selected, setSelected] = React.useState({
    cookies: false,
    pizza: false,
    icecream: false
  });
  const toggleSelected = key =>
    setSelected({
      ...selected,
      [key]: !selected[key]
    });

  return (
    <ChipSet filter>
      <Chip
        selected={selected.cookies}
        checkmark
        onClick={() => toggleSelected('cookies')}
        label="Cookies"
        trailingIcon="close"
      />
      <Chip
        selected={selected.pizza}
        checkmark
        onClick={() => toggleSelected('pizza')}
        icon="local_pizza"
        label="Pizza"
        trailingIcon="close"
      />
      <Chip
        selected={selected.icecream}
        checkmark
        onClick={() => toggleSelected('icecream')}
        icon="favorite_border"
        trailingIcon="close"
        label="Icecream"
      />
    </ChipSet>
  );
}

