function Example() {
  const [activeTab, setActiveTab] = React.useState(0);

  return (
    <TabBar
      activeTabIndex={activeTab}
      onActivate={(evt) => setActiveTab(evt.detail.index)}
    >
      <Tab>Cookies</Tab>
      <Tab>Pizza</Tab>
      <Tab>Icecream</Tab>
    </TabBar>
  );
}

function IconIndicatorExample() {
  const style = {
    transformOrigin: 'center center',
    transform: 'translateY(1rem) scale(0.45)'
  };

  return (
    <TabBar>
      <Tab
        label="Cookies"
        iconIndicator={{
          icon: 'star',
          style: style
        }}
      />
      <Tab
        label="Pizza"
        iconIndicator={{
          icon: 'favorite',
          style: style
        }}
      />
      <Tab
        label="Icecream"
        iconIndicator={{
          icon: 'mood',
          style: style
        }}
      />
    </TabBar>
  );
}
